<script>
import { Product } from 'src/models/Product'
import LazyImg from 'components/lazyImg'
import { useQuasar } from 'quasar'

export default {
  setup() {
    const $q = useQuasar()
    return { $q }
  },
  name: 'productItem',
  components: { LazyImg },
  data: () => ({
    product: new Product()
  }),
  props: {
    data: {
      type: Product,
      default: new Product()
    }
  },
  created() {
  },
  mounted() {
    this.product = new Product(this.data)
  },
  methods: {
    addToCart() {
      this.$store.dispatch('Cart/addToCart', this.product).then(() => {
        this.$store.dispatch('Cart/reviewCart', this.product).then(() => {
          this.$q.notify({
            message: 'با موفقیت به سبد خرید شما افزوده شد',
            color: 'green',
            actions: [
              {
                label: 'سبد خرید',
                icon: 'isax:shopping-cart',
                color: 'white',
                class: 'bg-green-3',
                handler: () => {
                  this.$router.push({ name: 'User.Checkout.Review' })
                }
              }
            ]
          })
        })
      })
    }
  }
}
