<script>
import { User } from 'src/models/User.js'
import LazyImg from 'src/components/lazyImg.vue'
import { APIGateway } from 'src/api/APIGateway.js'
import { mixinWidget, mixinAuthData } from 'src/mixin/Mixins.js'

export default {
  name: 'ProfileMenu',
  components: { LazyImg },
  mixins: [mixinWidget, mixinAuthData],
  data() {
    return {
      api: APIGateway.user.APIAdresses.base,
      user: new User(),
      file: null,
      previewImg: null,
      controls: false
    }
  },
  computed: {
    fullName() {
      if (!this.user || !this.user.full_name) {
        return 'وارد نشده'
      }

      return this.user.full_name
    }
  },
  created () {
    this.api = APIGateway.user.APIAdresses.base + '/' + this.user.id
  },
  mounted () {
    this.loadAuthData()
  },
  methods: {
    loadAuthData () { // prevent Hydration node mismatch
      this.user = this.$store.getters['Auth/user']
      this.previewImg = this.user.photo
    },
    runEvent (eventName) {
      this.$bus.emit(eventName)
    },
    isRouteSelected (itemName) {
      return (this.$route.name === itemName)
    },
    updatePhoto() {
      this.$refs.file.pickFiles()
    },
    updateFile() {
      this.controls = true
      this.previewImg = URL.createObjectURL(this.file)
    },
    discardUpdate() {
      this.controls = false
      this.file = null
      this.previewImg = this.user.photo
    },
    confirmUpdate() {
      const fd = new FormData()
      fd.append('photo', this.file)
      this.$axios.put(this.api, fd).then((d) => {
        this.controls = false
      })
    },
    logout() {
      this.$store.dispatch('Auth/logOut')
    }
  }
}
</script>

